import Head from "next/head";
import React, { useState } from "react";

export default function Home() {
  const [number1, setNumber1] = useState(0);
  const [number2, setNumber2] = useState(0);
  const [result, setResult] = useState("0");

  function calculate(operator = "") {
    let list = [];

    if (number1) list.push(number1);
    if (number2) list.push(number2);

    if (list.length === 0) {
      setResult("Please select fields");
      return;
    }

    let total = list[0];
    for (let idx = 1; idx < list.length; idx++) {
      if (operator === "/") total = number1 / number2;
    }

    setResult(total);
  }

  return (
    <div className="container" style={{ backgroundColor: "#EAEEF3" }}>
      <Head>
        <title>Calculator</title>
      </Head>

      <main>
        <div className="App">
          <br />
          <br />
          <h1
            style={{ textAlign: "center", maxWidth: "470px", margin: "0 auto" }}
          >
            Flexible payment structures designed for your business.
          </h1>
          <br />
          <br />
          <div
            className="container-form"
            style={{
              margin: "50px auto",
              backgroundColor: "white",
              maxWidth: "726px",
              display: "flex",
            }}
          >
            <div>
              <div>
                <label htmlFor="number1">Amount to finance</label>
              </div>
              <input
                type="number"
                value={number1}
                onChange={(e) => setNumber1(+e.target.value)}
                placeholder="0"
                className="form-control"
              />
            </div>

            <div>
              <div>
                <label htmlFor="number2">Term length</label>
              </div>
              <input
                type="number"
                value={number2}
                onChange={(e) => setNumber2(+e.target.value)}
                placeholder="0"
                className="form-control"
              />
            </div>
            <br />
          </div>

          <button
            type="button"
            className="btn btn-primary"
            onClick={() => {
              calculate("/");
            }}
          >
            {" "}
            /{" "}
          </button>
          <h2>{result}</h2>
        </div>
      </main>

      <style jsx global>{`
        html,
        body {
          background: rgba(234, 238, 243, 1);
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }
      `}</style>
    </div>
  );
}
