import Head from "next/head";
import React, { useState } from "react";

export default function Home() {
  const [number1, setNumber1] = useState(0);
  const [number2, setNumber2] = useState(0);
  const [result, setResult] = useState("0");

  function calculate(operator = "") {
    let list = [];

    if (number1) list.push(number1);
    if (number2) list.push(number2);

    if (list.length === 0) {
      setResult("Please select fields");
      return;
    }

    let total = list[0];
    for (let idx = 1; idx < list.length; idx++) {
      if (operator === "/") total = number1 / number2;
    }

    setResult(total);
  }

  return (
    <div className="container">
      <Head>
        <title>Calculator</title>
      </Head>

      <main>
        <div className="App" style={{ backgroundColor: "#EAEEF3" }}>
          <div
            className="container-form"
            style={{
              margin: "50px auto",
              backgroundColor: "white",
              maxWidth: "726px",
            }}
          >
            <br />
            <br />
            <h2>Simple calculator</h2>
            <br />
            <br />

            <div>
              <div>
                <label htmlFor="number1">Amount to finance</label>
              </div>
              <input
                type="number"
                value={number1}
                onChange={(e) => setNumber1(+e.target.value)}
                placeholder="0"
                className="form-control"
              />
            </div>

            <div>
              <div>
                <label htmlFor="number2">Term length</label>
              </div>
              <input
                type="number"
                value={number2}
                onChange={(e) => setNumber2(+e.target.value)}
                placeholder="0"
                className="form-control"
              />
            </div>

            <button
              type="button"
              className="btn btn-primary"
              onClick={() => {
                calculate("/");
              }}
            >
              {" "}
              /{" "}
            </button>
          </div>
          <br />

          <hr style={{ width: "50%", borderTop: "1px solid #b5b5b5" }} />
          <br />
          <h2>{result}</h2>
        </div>
      </main>
      <style jsx>{`
        .App {
          text-align: center;
        }

        .container-form {
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        .inline-form {
          display: flex;
          flex-direction: row;
        }

        .inline-form > div > label {
          margin-left: 15px;
        }

        div.form-control-lg {
          padding: 0.5rem 0;
          padding-left: 2.25rem;
        }

        .inline-form > button {
          width: 50px;
          margin: 10px;
        }

        .inline-form > button:first-child {
          margin-left: 0px;
        }

        label {
          cursor: pointer;
        }

        @media (max-width: 600px) {
          .grid {
            width: 100%;
            flex-direction: column;
          }
        }
      `}</style>

      <style jsx global>{`
        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }
      `}</style>
    </div>
  );
}
